/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <div align="center">
  <img alt="logo" src="/static/images/logo_720h.png" width="192" />
  <h1>VEDA</h1><i>VJ system on Atom</i>
  <br />
    </div>
    {
      /* ## Let your Atom blink. */
    }
    <p>{`VEDA is an Atom package for VJ / Livecoding with GLSL.`}</p>
    <p><img parentName="p" {...{
        "src": "https://user-images.githubusercontent.com/1403842/28673275-1d42b062-731d-11e7-92b0-bde5ca1f1cae.gif",
        "alt": null
      }}></img></p>
    <h3>{`Easy GLSL runtime`}</h3>
    <p>{`VEDA supports both fragment shaders and vertex shaders.
In fragment shaders, you can use uniform variables like `}<a target="\_blank" href="http://glslsandbox.com/">{`GLSL Sandbox`}</a>{`.
In vertex shaders, you can use uniform variables like `}<a target="\_blank" href="https://vertexshaderart.com/">{`vertexshaderart.com`}</a>{`.`}</p>
    <h3>{`Cross-platform by design`}</h3>
    <p>{`VEDA is built on web technologies and provided as an Atom package.
It works on Windows, macOS, and Linux.`}</p>
    <h3>{`Auto-completion, Linters, ...`}</h3>
    <p>{`Because VEDA runs on Atom, you can write GLSL with your favorite settings, package, and snippets.
Also, VEDA automatically installs `}<a target="\_blank" href="https://atom.io/packages/autocomplete-glsl">{`autocomplete-glsl`}</a>{` and `}<a target="\_blank" href="https://atom.io/packages/autocomplete-glsl">{`linter-glsl`}</a>{` for rapid GLSL development.`}</p>
    <h3>{`Audio, MIDI, OSC, and more!`}</h3>
    <p>{`VEDA supports a variety of inputs such as:`}</p>
    <ul>
      <li parentName="ul">{`Audio`}</li>
      <li parentName="ul">{`MIDI`}</li>
      <li parentName="ul">{`OSC`}</li>
      <li parentName="ul">{`WebCam`}</li>
      <li parentName="ul">{`Gamepad`}</li>
      <li parentName="ul">{`Keyboard`}</li>
      <li parentName="ul">{`Mouse`}</li>
    </ul>
    <br />
    {
      /* ## VEDA.js
      VEDA.js is a GLSL framework for modern web development.
      It was created as a part of VEDA, and published to npm later.
      [VEDA.js](/vedajs) */
    }
    {
      /* ## LICENSE
      Projects around VEDA is licensed in **MIT** license.
      So you can use them anywhere you want! */
    }
    <hr></hr>
    <p>{`If you like it, please post to Twitter with hashtag `}<inlineCode parentName="p">{`#VEDAJS`}</inlineCode>{`.
We'll be happy to see your tweets!ðŸ˜¸`}</p>
    <a href="https://twitter.com/intent/tweet?url=https://veda.gl/&hashtags=vedajs" target="\_blank">Tweet with #VEDAJS</a>
    </MDXLayout>;
}

