import React, { useContext } from "react";
import Link from "next/link";
import styled from "styled-components";
import { opacify } from 'polished';
import { AppContext, DispatchContext } from "../lib/context";
import constants from "./constants";
import config from "../config";
import Hamburger from './hamburger';

const Nav = styled.div`
  position: relative;
  width: 100%;
  height: 56px;
  transition: 0.5s;
  background: transparent;

  @media (max-width: ${constants.mobile}px) {
    &.visible {
      background: linear-gradient(
        to bottom,
        ${p => opacify(1, p.color)},
        ${p => p.color}
      );
    }
  }
`;

const Logo = styled.div`
  height: 100%;
  padding: 4px 0;
  position: absolute;
  left: 50%;
  transform: translateX(-50%);

  opacity: 0;
  @media (max-width: ${constants.mobile}px) {
    transition: opacity 1s;
    &.visible {
      opacity: 1;
    }
  }
`;

const Left = styled.div`
  position: absolute;
  left: 0;
`;
const Right = styled.div`
  position: absolute;
  display: flex;
  right: 0;
`;
const Button = styled.div`
  cursor: pointer;
  color: #ddd;
  user-select: none;
  & > img {
    display: block;
    width: 56px;
    height: 56px;
    padding: 15px;
    font-size: 24px;
    line-height: 27px;
  }
  span {
    padding: 15px;
    position: relative;
    display: block;
    top: -1em;
  }
  &:hover {
    opacity: 1;
    color: #fff;
  }

  .inner {
    position: absolute;
    top: 100%;
    right: 0;
    background: rgba(0, 0, 0, 0.8);
    color: #fff;
    text-align: center;
    a {
      text-decoration: none;
      color: white;
      &:hover {
        text-decoration: underline;
      }
    }
    i {
      margin-right: 10px;
      text-align: center;
    }
    display: none;
    opacity: 0;
    transition: opacity 0.5s;
    &.visible {
      width: 180px;
      display: block;
      opacity: 1;
    }
    &:before {
      content: "";
      position: absolute;
      top: -30px;
      right: 12px;
      border: 15px solid transparent;
      border-bottom: 15px solid black;
    }
  }
`;

const Header = (props) => {
  const state = useContext(AppContext);
  const dispatch = useContext(DispatchContext);

  const toggleMenu = () => dispatch({ type: 'toggleMenu' });
  const setLanguage = (lang) => dispatch({ type: 'setLanguage', lang });

  const cls = state.isHeaderVisible ? "visible" : "";

  // const { lang } = props;
  const lang = 'en';

  return (
    <Nav className={cls} color={state.color}>
      <Logo className={cls}>
        <Link href={lang === "en" ? "/" : `/?lang=${lang}`}>
          <a>
            <img src="/static/images/logo_header.png" alt="VEDA logo" />
          </a>
        </Link>
      </Logo>

      <Left className="mobile">
        <Hamburger active={state.isMenuVisible} onClick={toggleMenu} />
      </Left>
      <Right>
        <ul>
          {Object.entries(config.languages).map(([id, label]) =>
            <li key={id} onClick={() => setLanguage(id)}>{label}</li>
          )}
        </ul>
      </Right>
    </Nav>
  );
};

export default Header;
